mapper.select:
select id AS id, type AS type, cat_name AS catName, creator_id AS creatorId ,creator AS creator,create_time AS createTime FROM CMS_CAT WHERE 1=1
AND type = #{type}
ORDER BY id desc

select id AS id , type as type, cat_name AS catName,
 creator_id AS creatorId ,creator AS creator,create_time AS createTime  FROM CMS_CAT
 WHERE id = #{id}
