mapper.select:
select CMS_RULES.id AS id , CMS_RULES.title as title, CMS_RULES.content AS content, CMS_RULES.cat_id AS catId,cms_cat.cat_name AS catName, CMS_RULES.creator_id AS creatorId ,CMS_RULES.creator AS creator,CMS_RULES.create_time AS createTime FROM CMS_RULES LEFT JOIN cms_cat ON CMS_RULES.cat_id = cms_cat.id WHERE 1=1
AND CMS_RULES.cat_id = #{catId}
ORDER BY CMS_RULES.id DESC

select id AS id , title as title, content AS content, cat_id AS catId,
 creator_id AS creatorId ,creator AS creator,create_time AS createTime  FROM CMS_RULES
 WHERE id = #{id}
