mapper.update:
update et_user_exam_history

	answer_sheet = #{answerSheetStr},point=#{answerSheet.pointMax},point_get=#{answerSheet.pointRaw},submit_time=sysdate(),duration=#{answerSheet.duration},
	start_time=#{answerSheet.startTime}

		,approved=#{approved}


where id=#{answerSheet.examHistroyId}

mapper.select:
select m.*,p.dep_name depName from (select a.id histId,a.user_id userId,b.user_name userName,b.true_name trueName,b.phone_num phoneNum,b.email,b.national_id nationalId,a.start_time startTime,a.exam_id examId,a.exam_paper_id examPaperId,a.enabled,c.total_point point,a.seri_no seriNo,a.content,
a.answer_sheet answerSheet,a.duration,a.point_get pointGet,a.submit_time submitTime,a.approved,a.verify_time verifyTime,c.pass_point passPoint,c.name examPaperName from et_user_exam_history a,et_user b,et_exam_paper c
where a.user_id=b.user_id and a.exam_id=#{examId} and a.exam_paper_id=c.id

	order by a.point_get ${order}


	limit 0,#{limit}

) m left join et_user_2_department n on m.userId = n.user_id left join et_department p on n.dep_id=p.dep_id

	where (instr(m.trueName,#{searchStr}) <> 0 or instr(m.userName,#{searchStr}) <> 0 or instr(m.phoneNum,#{searchStr}) <> 0 or instr(m.nationalId,#{searchStr}) <> 0 or instr(m.email,#{searchStr}) <>0 or instr(m.seriNo,#{searchStr}) <> 0 or instr(p.dep_name,#{searchStr}) <> 0)

select m.*,p.dep_name depName from (select a.id histId,a.user_id userId,b.user_name userName,b.true_name trueName,a.start_time startTime,a.exam_id examId,a.exam_paper_id examPaperId,a.enabled,a.point,a.seri_no seriNo,a.content, a.answer_sheet answerSheet,a.duration,a.point_get pointGet,a.submit_time submitTime,a.approved,a.verify_time verifyTime from et_user_exam_history a,et_user b where a.user_id=b.user_id
and a.approved in
#{item}
) m left join et_user_2_department n on m.userId = n.user_id left join et_department p on n.dep_id=p.dep_id order by m.histId desc

select a.id histId,a.user_id userId,b.user_name userName,b.true_name trueName,a.start_time startTime,a.exam_id examId,a.exam_paper_id examPaperId,a.enabled,a.point,a.seri_no seriNo,a.content,
a.answer_sheet answerSheet,a.duration,a.point_get pointGet,a.submit_time submitTime,a.approved,a.verify_time verifyTime from et_user_exam_history a,et_user b
where a.user_id=b.user_id and a.id=#{histId}

select a.id histId,a.user_id userId,b.user_name userName,b.true_name trueName,a.start_time startTime,a.exam_id examId,a.exam_paper_id examPaperId,a.enabled,a.point,a.seri_no seriNo,a.content, a.answer_sheet answerSheet,a.duration,a.point_get pointGet,a.submit_time submitTime,a.approved,a.verify_time verifyTime from et_user_exam_history a,et_user b where a.user_id=b.user_id and a.user_id=#{userId} and a.exam_id=#{examId}
and a.approved in
#{item}

select id answerSheetId, exam_history_id examHistroyId,exam_id examId,exam_paper_id examPaperId,user_id userId, times times, sum_score sumScore, create_time createTime, creator_id creatorId, modify_time modifyTime, modifier_id modifierId
from et_answer_sheet 
where user_id=#{studentId} and times = #{times}

select id answerSheetId, exam_history_id examHistroyId,exam_id examId,exam_paper_id examPaperId,user_id userId, times times, sum_score sumScore, create_time createTime, creator_id creatorId, modify_time modifyTime, modifier_id modifierId, creator_name creatorName
from et_answer_sheet a 
where a.user_id=#{studentId}

select id answerSheetItemId, answer_sheet_id answerSheetId, point point,question_type_id questionTypeId,answer answer,question_id questionId,comment comment, student_id studentId, teacher_id teacherId, assess_time assessTime, knowlege_point_id knowlegePointId
from et_answer_sheet_item
where id=#{answerSheetItemId}

select id answerSheetItemId, answer_sheet_id answerSheetId, point point,question_type_id questionTypeId,answer answer,question_id questionId,comment comment, student_id studentId, teacher_id teacherId, assess_time assessTime, knowlege_point_id knowlegePointId, score score
from et_answer_sheet_item a 
where a.answer_sheet_id=#{answerSheetId}

select id answerSheetItemId, answer_sheet_id answerSheetId, point point,question_type_id questionTypeId,answer answer,question_id questionId, question_code questionCode,comment comment, student_id studentId, teacher_id teacherId, assess_time assessTime, knowlege_point_id knowlegePointId, score
from et_answer_sheet_item a 
where a.student_id=#{studentId} and a.times=#{times}

	and a.knowlege_point_code=#{pointCode}
